run_analysis.R <- function() {
        library(data.table); library(plyr); library(dplyr); library(qdap)
        features <- read.table("features.txt")
        
        testDF <- data.frame(x = read.table("./test/X_test.txt", col.names = c(as.vector(features$V2))), y = read.table("./test/y_test.txt"), z = read.table("./test/subject_test.txt"))
        
        trainDF <- data.frame(x = read.table("./train/X_train.txt", col.names = c(as.vector(features$V2))), y = read.table("./train/y_train.txt"), z = read.table("./train/subject_train.txt"))
        
        my_mergedtables <- merge(testDF, trainDF, all = TRUE)
        
        
        my_mergedtablesedit <- my_mergedtables %>% select(c(1:6, 41:46, 81:86, 121:126, 161:166, 201:202, 214:215, 227:228, 240:241, 253:254, 266:271, 345:350, 424:429, 503:504, 516:517, 529:530, 542:543, V1, V1.1)) %>% mutate(activity = multigsub(c("1", "2", "3", "4", "5", "6"), c("walking", "walking_upstairs", "walking_downstairs", "sitting", "standing", "laying"), my_mergedtables$V1)) %>% rename(subject_id_number = V1.1)
        
        names(my_mergedtablesedit) <- gsub("x.", "", names(my_mergedtablesedit))
        
        my_mergedtablesedited <- select(my_mergedtablesedit, c(1:66, 68:69))
        
        my_groups <- group_by(my_mergedtablesedited, subject_id_number, activity)
        
        
